hudson.tasks.Shell.command:
if [ $deployToProduction ] then SSH_KEY=development else SSH_KEY=production fi
# Set Stack Name and Application Name ApplicationName=Target-${BUILD_NUMBER} StackName=Target-${BUILD_NUMBER}
# Set Domain Name Domain=`ruby ${WORKSPACE}/lib/showback_domain.rb --item ${properties} \ --key DOMAIN`
ruby ${WORKSPACE}/lib/set_properties.rb --property StackName \ --value ${StackName} \ --itemname ${StackName}
# Create Cloudformaton Stack ruby ${WORKSPACE}/lib/create_stack.rb --stackname ${StackName} \ --domain ${Domain} \ --templatelocation ${WORKSPACE}/infrastructure/target/production.template \ --application ${ApplicationName} \ --sshkey ${SSH_KEY} \ --s3bucket ${S3BUCKET} \ --securitygroup ${SGID} \ --snstopic ${SNS_TOPIC}
# Load SimpleDB Domain with Key/Value Pairs ruby ${WORKSPACE}/lib/load_domain.rb --filename ${StackName} \ --itemname ${StackName}
# Pull and store variables from SimpleDB host=`ruby ${WORKSPACE}/lib/showback_domain.rb --item ${StackName} \ --key InstanceIPAddress`
# Run Acceptance Tests cucumber ${WORKSPACE}/infrastructure/target/features/production.feature host=${host} user=ec2-user key=/usr/share/tomcat6/.ssh/id_rsa
# Publish notifications to SNS sns-publish --topic-arn $SNS_TOPIC \ --subject "New Environment Ready" \ --message "Your new environment is ready. IP Address: $host. An example command to ssh into the box would be: ssh -i development.pem ec2-user@$host This instance was created by $JENKINS_DOMAIN" --aws-credential-file /usr/share/tomcat6/aws_access
